import React from 'react';
import { Swiper, SwiperSlide } from 'swiper/react';
import "../App.css";

import { Pagination, Navigation } from 'swiper/modules';

const MySwiper = () => {
  const slides = [
    {
      imageUrl: 'https://via.placeholder.com/800x400?text=Slide+1',
      caption: 'ghgvhvjvjhvjhbjhbhjvj',
    },
    {
      imageUrl: 'https://via.placeholder.com/800x400?text=Slide+2',
      caption: 'Slide 2 Caption',
    },
    {
      imageUrl: 'https://via.placeholder.com/800x400?text=Slide+3',
      caption: 'Slide 3 Caption',
    },
    {
      imageUrl: 'https://via.placeholder.com/800x400?text=Slide+4',
      caption: 'Slide 4 Caption',
    },
  ];

  return (
    <Swiper
      spaceBetween={30}
      slidesPerView={1}
      loop={true}
      pagination={{ clickable: true }} // Enable clickable pagination bullets
      navigation={true}
      modules={[Pagination, Navigation]}
    >
      {slides.map((slide, index) => (
        <SwiperSlide key={index}>
          <img src={slide.imageUrl} alt={slide.caption} style={{ width: '100%', height: 'auto' }} />
          <p style={{ textAlign: 'center', marginTop: '10px' }}>{slide.caption}</p>
        </SwiperSlide>
      ))}
    </Swiper>
  );
};

export default MySwiper;
